var express = require('express');
var router = express.Router();
var database = require('./dao');
var dao = new database.Dao();
        
/**
 * @swagger
 * /searchProduct:
 *   get:
 *     tags:
 *       - Administration
 *     description: searchProduct
 *     summary: ProductSearch
 *     consumes:
 *       - application/json
 *     produces:
 *       - application/json
 *     parameters: 
 *       - name: id
 *         description: Integer
 *         in: query
 *         type: Integer
 *       - name: code
 *         description: String
 *         in: query
 *         type: String
 *       - name: parent
 *         description: String
 *         in: query
 *         type: String
 *       - name: name
 *         description: String
 *         in: query
 *         type: String
 *       - name: description
 *         description: String
 *         in: query
 *         type: String
 *       - name: discountCategory
 *         description: String
 *         in: query
 *         type: String
 *       - name: taxId
 *         description: Integer
 *         in: query
 *         type: Integer
 *       - name: serialised
 *         description: Integer
 *         in: query
 *         type: Integer
 *     responses:
 *       '200':
 *         description: OK
 *       '401':
 *         description: Unauthorized
 *       '403':
 *         description: Forbidden
 *       '404':
 *         description: Not Found 
 *       '500':
 *         description: Internal Server Problem
 */    

router.get('/', function(req, res, next) {
    var jsonObj = req.query;
    if(jsonObj.rowid===undefined) jsonObj.rowid = 0;
    dao.searchProduct(jsonObj,function(err,rows) {    
        if(err) res.status(err.code).end();
        else {
            jsonObj.product = rows;
            res.status(200).send(jsonObj);
        }
        console.log(jsonObj);        
    });
});

module.exports = router;
